	int l = 1, r = n;

	go(i,1,n){

		scanf("%d",&a[i]);

		c[i] = a[i];

	}

	sort(c+1,c+n+1);

	int ans = 0;

	while(l <= r){

		int mid = MID(l,r);

		if (find(mid)) l = mid+1,ans = c[mid]; else r = mid - 1;

	}

	printf("%d\n",ans);

	return 0;

}

