         while((c < '0' || c > '9') && c != '-' && c != EOF){

             c = getchar();

         }

         bool neg = false;

         if(c == '-'){

             neg = true;

             c = getchar();

         }

         while(c >= '0' && c <= '9'){

             x = x * 10 + (c - '0');

             c = getchar();

         }

         if(neg) x = -x;

