#include <bits/stdc++.h>

using namespace std;

const ll INF = LLONG_MAX;

vector<ll> arr;
vector<ll> dp;
ll k2;

ll func(ll n, ll cur) {
    if (cur == n) return 0;
    if (dp[cur] != -1) return dp[cur];

    ll sum = INF;
    for (ll i = 1; i <= k2 && cur + i <= n; ++i) {
        sum = min(sum, func(n, cur + i) + abs(arr[cur] - arr[cur + i]));
    }

    return dp[cur] = sum;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0); // Speed up input/output

    ll n; 
    cin >> n >> k2;

    arr.resize(n);
    for (ll &val : arr) cin >> val;

    dp.assign(n, -1);
    cout << func(n - 1, 0) << endl;

    return 0;
}
