#include<iostream>
#include<cmath>
#include<vector>
#include<algorithm>

using namespace std;

int main()
{
    int n, k;
    cin >> n >> k;

    vector<int> w(n + 1);
    vector<int> f(n + 1, INT_MAX);

    for(int i = 1; i <= n; ++i)
    {
        cin >> w[i];
    }

    f[1] = 0;

    for(int i = 2; i <= n; ++i)
    {
        for(int j = max(1, i - k); j < i; ++j)
        {
            f[i] = min(f[i], f[j] + abs(w[j] - w[i]));
        }
    }

    cout << f[n] << endl;

    return 0;
}
