#include <iostream>
#include <vector>

using namespace std;

int main() {
    vector<bool> a(1000010, false);
    int s;
    cin >> s;
    a[s] = true;

    for(int i = 1; i < 1000000; ++i) {
        if(s % 2 == 0) {
            s /= 2;
            if(a[s]) {
                cout << i + 1 << endl;
                return 0;
            }
            a[s] = true;
        } else {
            s = s * 3 + 1;
            if(a[s]) {
                cout << i + 1 << endl;
                return 0;
            }
            a[s] = true;
        }
    }

    return 0;
}
