#include <iostream>
#include <vector>
#include <limits>
using namespace std;

typedef long long int ll;

vector<int> memo(1000000, numeric_limits<int>::max());

void solve(){
    int n, k; cin >> n >> k;
    vector<int> a(n);
    for (int& ai : a) {
        cin >> ai;
    }

    memo[0] = 0;
    for (int i = 0; i < n; ++i) {
        int limit = min(n, i + k + 1); // Loop limit defined outside inner loop
        for(int j = i + 1; j < limit; ++j){
            memo[j] = min(memo[j], memo[i] + abs(a[i] - a[j]));
        }
    }

    cout << memo[n - 1] << "\n";
}

int main(){
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    solve();
    return 0;
}
