#include <iostream>
#include <unordered_set>

using namespace std;

typedef long long ll;

int main() {
    int s;
    cin >> s;
    unordered_set<int> seen;

    int current = s;
    int i = 1;
    while (true) {
        if (seen.find(current) != seen.end()) {
            cout << i << endl;
            return 0;
        }
        seen.insert(current);

        if (current % 2 == 0) {
            current /= 2;
        } else {
            current = current * 3 + 1;
        }

        i++;
    }
}
