#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

#define ll long long
#define fast_io ios::sync_with_stdio(false); cin.tie(nullptr)
const ll MOD = 1e9 + 7;
const int INF = 1 << 30;

int main() {
    fast_io;
    int N, K;
    cin >> N >> K;
    vector<int> h(N + 1);
    vector<int> dp(N + 1, INF);

    for (int i = 1; i <= N; i++) {
        cin >> h[i];
    }

    dp[1] = 0;
    for (int i = 1; i < N; i++) {
        for (int j = 1; j <= K && i + j <= N; j++) {
            dp[i + j] = min(dp[i + j], dp[i] + abs(h[i + j] - h[i]));
        }
    }

    cout << dp[N] << endl;
    return 0;
}
