#include <iostream>
#include <unordered_set>
using namespace std;

int main() {
    int s;
    cin >> s;

    unordered_set<int> visited;
    visited.insert(s);

    for (int i = 2; ; ++i) {
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = s * 3 + 1;
        }
        if (visited.count(s)) {
            cout << i << endl;
            break;
        }
        visited.insert(s);
    }

    return 0;
}
