#include <iostream>
#include <unordered_set>
using namespace std;

int main() {
  int s;
  cin >> s;

  unordered_set<int> seen;
  int current = s;
  seen.insert(current);
  int steps = 1;

  while (true) {
    if (current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }

    steps++;
    if (seen.find(current) != seen.end()) {
      cout << steps << endl;
      return 0;
    }
    seen.insert(current);
  }
}
