#include <bits/stdc++.h>

#define ll long long int
#define MAX 1000006
#define INF 1e9
#define fastRead ios_base::sync_with_stdio(false);cin.tie(nullptr);cout.tie(nullptr)

using namespace std;

int n, k;
ll ara[MAX], dp[MAX];

ll solve(int id) {
    if (id == n - 1) return 0;
    ll &ret = dp[id];
    if (ret != -1) return ret;

    ret = INF;
    for (int i = 1; i <= k && id + i < n; i++) {
        ret = std::min(ret, std::abs(ara[id + i] - ara[id]) + solve(id + i));
    }
    return ret;
}

int main() {
    fastRead;

    cin >> n >> k;
    for (int i = 0; i < n; i++) cin >> ara[i];

    std::fill(dp, dp + n, -1);

    cout << solve(0) << endl;

    return 0;
}
