#include <iostream>
#include <vector>
#include <string>
using namespace std;

int main() {
    int N, M;
    cin >> N >> M;
    vector<int> flag(N + 1, 0), penalty(N + 1, 0);
    int P;
    string S;

    for (int i = 0; i < M; i++) {
        cin >> P >> S;
        if (S == "AC") flag[P] = 1;
        else if (flag[P] == 0) penalty[P]++;
    }

    int sum1 = 0, sum2 = 0;

    for (int i = 1; i <= N; i++) {
        if (flag[i]) {
            sum1++;
            sum2 += penalty[i];
        }
    }

    cout << sum1 << " " << sum2 << endl;
    return 0;
}
