#include <iostream>
#include <vector>
using namespace std;
typedef long long ll;

int main() {
    ll N, K;
    cin >> N >> K;

    if (K == 0) {
        cout << N * N << endl;
        return 0;
    }

    ll cnt = 0;
    // Loop from K+1 to N to calculate the contribution for each i
    for (ll i = K + 1; i <= N; ++i) {
        cnt += (N / i) * (i - K) + max(0LL, N % i - K + 1);
    }

    cout << cnt << endl;
    return 0;
}
