#include <bits/stdc++.h>

#define endl '\n'
#define ll long long

using namespace std;

inline void fast() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);
}

ll arr[10000000];
vector<ll> v(1e6, -1);

ll minCost(int indx, int n, int k) {
    if (indx == n - 1) {
        return 0;
    }
    if (v[indx] != -1) {
        return v[indx];
    }
    ll path1 = LLONG_MAX;
    int limit = min(indx + k + 1, n);
    for (int i = indx + 1; i < limit; ++i) {
        path1 = min(path1, abs(arr[indx] - arr[i]) + minCost(i, n, k));
    }
    return v[indx] = path1;
}

int main() {
    fast();

    int n, k;
    cin >> n >> k;

    for (int i = 0; i < n; ++i) {
        cin >> arr[i];
    }

    cout << minCost(0, n, k) << endl;

    return 0;
}
