#include <bits/stdc++.h>

#define ll long long
#define MAX_NUM 1000000000

using namespace std;

ll h[1000000];
ll dp[1000000];
ll n, k;

ll fun(ll i) {
    if (i >= n) {
        return MAX_NUM;
    }
    if (i == n - 1) {
        return 0;
    }
    if (dp[i] != -1) {
        return dp[i];
    }
    ll minimum = MAX_NUM;
    for (ll j = 1; j <= k && i + j < n; j++) {
        ll temp = abs(h[i] - h[i + j]) + fun(i + j);
        minimum = min(minimum, temp);
    }
    return dp[i] = minimum;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    cin >> n >> k;
    for (ll i = 0; i < n; i++) {
        cin >> h[i];
    }

    fill(dp, dp + n, -1);
    ll ans = fun(0);
    cout << ans << '\n';
    return 0;
}
