#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>
#include <climits>

using namespace std;

int main() {
    std::ios::sync_with_stdio(false);
    ll n = 0, k2 = 0;

    cin >> n >> k2;

    vector<ll> arr(n);
    vector<ll> dp(n, LLONG_MAX);

    for (ll i = 0; i < n; ++i) {
        cin >> arr[i];
    }

    dp[0] = 0;

    for (ll i = 0; i < n; ++i) {
        for (ll j = 1; j <= k2 && i + j < n; ++j) {
            dp[i + j] = min(dp[i + j], dp[i] + abs(arr[i] - arr[i + j]));
        }
    }

    cout << dp[n - 1] << endl;

    return 0;
}
