#include <iostream> // cout, endl, cin
#include <set> // set

using namespace std;
typedef long long ll;

int main(){
  int s;
  cin >> s;

  set<int> st;
  st.insert(s);

  int current = s;
  int steps = 1;

  while (true) {
    if (current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }

    if (st.count(current)) {
      cout << steps + 1 << endl;
      break;
    }

    st.insert(current);
    steps++;
  }

  return 0;
}
