#include <iostream>
#include <vector>
#include <algorithm>

#define ll long long

using namespace std;

int main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);

    int n, m;
    cin >> n >> m;

    vector<ll> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<ll> dp(n, 1e9);
    dp[n-1] = 0;

    for (int i = n - 2; i >= 0; i--) {
        for (int j = 1; j <= m && i + j < n; j++) {
            dp[i] = min(dp[i], dp[i + j] + abs(a[i] - a[i + j]));
        }
    }

    cout << dp[0] << endl;

    return 0;
}
