#include <bits/stdc++.h>

using namespace std;

#define ft ios_base::sync_with_stdio(false);cin.tie(nullptr);
typedef long long int ll;

vector<int> d;

void solve(){

    int n, k; cin >> n >> k;
    vector<int> a(n + 1);
    d.resize(n + 2, INT_MAX);

    for (int i = 1; i <= n; ++i)
    {
        cin >> a[i];
    }

    d[1] = 0;
    for (int i = 1; i <= n; ++i)
    {
        for(int m = 1; m <= k; m++){
            int j = i + m;
            if(j <= n) {
                d[j] = min(d[j], d[i] + abs(a[i] - a[j]));
            }
        }
    }
    cout << d[n] << "\n";
}

int main(){
    ft;
    // ll t; cin >> t;
    // while(t--)
    solve();
    return 0;
}
