#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

#define ll long long
ll INFL = 1LL << 60;

int main(){
    int N, K;
    cin >> N >> K;
    vector<int> h(N);
    vector<ll> dp(N, INFL);
    for(int i = 0; i < N; i++) cin >> h[i];

    dp[0] = 0;

    for(int i = 0; i < N - 1; i++){
        for(int j = 1; j <= K && i + j < N; j++){
            dp[i + j] = min(dp[i + j], dp[i] + abs(h[i + j] - h[i]));
        }
    }

    cout << dp[N - 1] << endl;
}
