#include <bits/stdc++.h>

using namespace std;

#define INF 1e15
#define endl "\n"
#define vi vector<int>
#define For(i,s,e) for (int i=(s); i<(e); i++)

int dp[500005], arr[500005];
int k;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    int n;
    cin >> n >> k;

    For(i, 1, n+1) cin >> arr[i];

    fill(dp, dp+n+1, INF);
    dp[1] = 0;

    For(i, 2, n+1) {
        For(j, max(1, i-k), i) {
            dp[i] = min(dp[i], dp[j] + abs(arr[i] - arr[j]));
        }
    }

    cout << dp[n] << endl;

    return 0;
}
