#include <bits/stdc++.h>

#define MAXN 1000010
#define INF 0x3f3f3f3f
using namespace std;

long long dp[MAXN], arr[MAXN];
long long n, k;

int main() {
    fill(dp, dp + MAXN, INF);
    scanf("%lld%lld", &n, &k);

    for (int i = 1; i <= n; i++) {
        scanf("%lld", &arr[i]);
    }

    dp[1] = 0;
    for (int i = 2; i <= n; i++) {
        for (int j = i - 1; j >= max(i - k, 1LL); j--) {
            dp[i] = min(dp[i], dp[j] + abs(arr[i] - arr[j]));
        }
    }

    cout << dp[n] << "\n";
    return 0;
}
