#include <iostream>
#include <vector>
#include <algorithm>

#define MAX 1000006
#define INF 1e9

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;
    vector<int> ara(n);
    vector<int> dp(n, INF);

    for (int i = 0; i < n; i++) cin >> ara[i];

    dp[n - 1] = 0;
    for (int i = n - 2; i >= 0; i--) {
        for (int j = 1; j <= k && i + j < n; j++) {
            dp[i] = min(dp[i], abs(ara[i + j] - ara[i]) + dp[i + j]);
        }
    }

    cout << dp[0] << endl;
    return 0;
}
