#define _CRT_SECURE_NO_WARNINGS
#include <iostream>
#include <cmath>
#include <queue>
#include <string>

using namespace std;

int N, M, A[1 << 18];
bool used[1 << 18];

int main() {
    cin >> N >> M;
    for (int i = 1; i <= M; i++) {
        int a;
        string b;
        cin >> a >> b;
        if (b == "AC") {
            used[a] = true;
        } else if (!used[a]) {
            A[a]++;
        }
    }
    int v1 = 0, v2 = 0;
    for (int i = 1; i <= N; i++) {
        if (!used[i]) continue;
        v1++;
        v2 += A[i];
    }
    cout << v1 << " " << v2 << endl;
    return 0;
}
