#include <bits/stdc++.h>

using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    int n, k;
    cin >> n >> k;
    vector<long long> h(n);
    vector<long long> dp(n + 1, LLONG_MAX);
    for (int i = 0; i < n; i++) {
        cin >> h[i];
    }
    dp[n - 1] = 0; // Starting point
    for (int i = n - 2; i >= 0; --i) { // Iterative dp calculation
        for (int j = 1; j <= k && i + j < n; ++j) {
            dp[i] = min(dp[i], abs(h[i] - h[i + j]) + dp[i + j]);
        }
    }
    cout << dp[0] << "\n";
    return 0;
}
