inline int lb(int x) {

    return x & -x;

}



void add(ll num, int x) {

    for (int i = x; i <= n; i+=lb(i))

        c[i] += num;

}



ll read(int x) {

    ll tot = 0;

    for (int i = x; i; i -= lb(i))tot += c[i];

    return tot;

}



const int cmp(const int i, const int j) {

    return f[j] == f[i] ? i < j : f[i] < f[j];

}



ll inversed_pair() {

    memset(c, 0, sizeof(c));

    for (int i = 1; i <= n; ++i)r[i] = i;

    sort(r + 1, r + 1 + n, cmp);

    ll tot = 0;

    for (int i = 1; i <= n; ++i) {

        tot += read(r[i]);

        add(1, r[i]);

    }

    return tot;

}



bool check(int x) {

    for (int i = 1; i <= n; ++i) {

        if (a[i] >= x)b[i] = 1;

        else b[i] = 0;

    }

    for (int i = 1; i <= n; ++i) {

        s[i] = s[i - 1] + b[i];

    }

    int t = 0;

    for (int i = 1; i <= n; ++i) {

        f[i] = 2 * s[i] - i;

        if (f[i] >= f[0])++t;

    }

    return (2 * (inversed_pair() + t) >= m);

}



