#include <bits/stdc++.h>

using namespace std;

typedef long long int ll;

void solve() {
    int n, k; cin >> n >> k;

    vector<int> a(n + 1); // Changed to 1-indexed

    for (int i = 1; i <= n; ++i) { // Changed to 1-indexed
        cin >> a[i];
    }

    vector<int> memo(n + 2, INT_MAX); // Dynamic size of memo array
    memo[1] = 0; // Start from index 1

    for (int i = 1; i <= n; ++i) { // Changed to 1-indexed
        for (int j = i + 1; j <= min(i + k, n); j++) { // Ensure j doesn't exceed n
            memo[j] = min(memo[j], memo[i] + abs(a[i] - a[j]));
        }
    }

    cout << memo[n] << "\n"; // Output the result from index n
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    solve();
