void Add(int x) {
    while (x <= 4 * n) {
        f[x] += 1;
        x += (x & (-x));
    }
}

int Query(int x) {
    int ans = 0;
    while (x > 0) {
        ans += f[x];
        x -= (x & (-x));
    }
    return ans;
}

bool check(int min_val, int max_val) {
    std::memset(f, 0, sizeof(f));
    long long Ans = 0;
    b[0] = 0;

    for (int i = 1; i <= n; i++) {
        b[i] = (a[i] >= min_val) ? (b[i - 1] + 1) : b[i - 1];
    }

    for (int i = 0; i <= n; i++) {
        b[i] = 2 * b[i] - i + n + 1;
        Ans += Query(b[i]);
        Add(b[i]);
    }

    long long t = (1LL * n * (n + 1)) / 2;
    return Ans >= (t + 1) / 2;
}

