int main(){
  int s;
  cin >> s;
  vector<int> a(10000, 0); // Reduced size to 10,000

  a.at(s)++;
  for(int i=2; i<10000; i++){ // Adjusted loop limit to match vector size
    if(s % 2 == 0){
      s /= 2;
      if(s < 10000)  // Check to prevent out of bounds access
        a.at(s)++;
    }else{
      s *= 3;
      s++;
      if(s < 10000)  // Check to prevent out of bounds access
        a.at(s)++;
    }

    if(s < 10000 && a.at(s) == 2){ // Check to ensure we are within bounds
      cout << i << endl;
      break;
    }
  }

  return 0;
}

