#include <cstdio>
#include <algorithm>
#include <iostream>
#define ri register int 
#define ll long long 
using std::min;
using std::max;
const int maxn=200005;
const int inf=0x7fffffff;
int n,a[maxn],b[maxn];
int mx=-inf,mi=inf;
ll sum[maxn];

inline void update(int x,int d) {
    for(; x <= n; x += x & (-x))
        sum[x] += d;
}

inline ll query(int x) {
    ll ans = 0;
    for(; x; x -= x & (-x))
        ans += sum[x];
    return ans;
}

inline ll chk(int k) {
    ll ans = 0;
    b[0] = 0;
    int count = 0;
    for(ri i = 1; i <= n; i++) {
        b[i] = b[i - 1] + ((a[i] <= k) ? 1 : -1);
        count += (b[i] > 0);
    }
    std::fill(sum, sum + n + 1, 0);
    for(ri i = 1; i <= n; i++) {
        ans += query(b[i] - 1);
        update(b[i], 1);
    }
    return ans;
}

inline void read_input() {
    std::cin >> n;
    for(ri i = 1; i <= n; i++) {
        std::cin >> a[i];
        mi = min(mi, a[i]), mx = max(mx, a[i]);
    }
}

int main() {
    read_input();
    int L = mi, R = mx, mid, ans;
    ll lim = 1ll * n * (n + 1) / 4 + 1;
    while(L <= R) {
        mid = (L + R) >> 1;
        if(chk(mid) >= lim) {
            ans = mid;
            R = mid - 1;
        } else {
            L = mid + 1;
        }
    }
    printf("%d\n", ans);
    return 0;
}

