#include<cstdio>
#include<cstring>
using namespace std;

int n, m, f[1100000], w[1100000], k;

int main ()
{
	cin >> n >> k;
	memset(f, 0x3f, sizeof(int) * (k + 1)); // Only initialize the first k elements
	for(int i = 0; i < n; i++)
	{
		cin >> w[i];
	}
	f[0] = 0;
	for(int i = 1; i < n; i++)
	{
		for(int j = 1; j <= k; j++)
		{
			if(i - j < 0) break;
			f[i] = min(f[i], f[i - j] + abs(w[i - j] - w[i]));
		}
	}
	cout << f[n - 1];
}

