        if (Ans >= (1LL * n * (n + 1)) / 4) return true;
    }

    // Calculate Ans using the pre-stored indices
    for(int i = 0; i <= n; i++) {
        if (r[b[i]] > 0) 
            Ans += Query(b[r[b[i]]]);
        // Early exit if condition is satisfied
        if (Ans >= (1LL * n * (n + 1)) / 4) return true;
    }

    return Ans >= ((1LL * n * (n + 1)) / 2 + 1) / 2;
}

