    for (int i = 0; i <= n; i++) {
        b[i] = 2 * b[i] - i + n + 1;
        Ans += Query(b[i]);
        Add(b[i]);
    }

    long long t = (1LL * n * (n + 1)) / 2;
    return Ans >= (t + 1) / 2;
}

int main() {
    std::cin >> n;
    int l = INT_MAX, r = INT_MIN;

    for (int i = 1; i <= n; i++) {
        std::cin >> a[i];
        l = std::min(l, a[i]);
        r = std::max(r, a[i]);
    }

    int ans = 0;
    int low_val = l, high_val = r;

    while (low_val <= high_val) {
        int mid = (low_val + high_val) >> 1;
        if (check(mid, mid)) low_val = mid + 1, ans = mid;
        else high_val = mid - 1;
    }

    std::cout << ans << '\n';
    return 0;
}

