#define LOWBIT(x) (x & -(x))

using namespace std;

const int MAXN = 1e6 + 7;

struct Element {
    int value;
    int position;
};

Element a[MAXN], sum[MAXN], c[MAXN];
int n;
long long ans0;

void add(int x) {
    while (x <= 5 * n) {
        c[x].value += 1;
        x += LOWBIT(x);
    }
}

int get(int x) {
    int y = 0;
    while (x > 0) {
        y += c[x].value;
        x -= LOWBIT(x);
    }
    return y;
}

bool judge(int x) {
    sum[0].value = 0;
    for (int i = 1; i <= n; i++) {
        sum[i].value = sum[i - 1].value + (a[i].value >= x);
    }
    for (int i = 0; i <= n; i++) {
        sum[i].value = 2 * sum[i].value - i + n + 1;
    }
    long long ans1 = 0;
    mem(c, 0);
    for (int i = 0; i <= n; i++) {
        ans1 += get(sum[i].value);
        add(sum[i].value);
    }
    return ans1 >= ans0;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i].value);
        a[i].position = i;
    }
    sort(a + 1, a + n + 1, [](const Element &x, const Element &y) { return x.value < y.value; });
    int l = 1, r = n, ans = 0;
    ans0 = (1LL * n * (n - 1) / 2 + n + 1) / 2LL;
    while (l <= r) {
        int mid = (l + r) / 2;
        if (judge(a[mid].value)) ans = mid, l = mid + 1;
        else r = mid - 1;
    }
    printf("%d\n", a[ans].value);
    return 0;
}

