#include <bits/stdc++.h>

#define endl "\n"
#define ll long long int
#define vi vector<int>
#define vll vector<ll>
#define vvi vector < vi >
#define pii pair<int,int>
#define pll pair<long long, long long>
#define mod 1000000007
#define inf 1000000000000000001
#define all(c) c.begin(),c.end()
#define mp(x,y) make_pair(x,y)
#define mem(a,val) memset(a,val,sizeof(a))
#define pb push_back
#define f first
#define se second

using namespace std;

ll k2;
vector<ll> arr;

int main() {
    std::ios::sync_with_stdio(false);
    ll n = 0;
    cin >> n >> k2;

    arr.resize(n);
    for (ll i = 0; i < n; ++i) {
        cin >> arr[i];
    }

    vector<ll> dp(n, inf);
    dp[n - 1] = 0;
    deque<ll> dq;
    dq.push_back(n - 1);

    for (ll cur = n - 2; cur >= 0; --cur) {
        // Remove indices that are not reachable
        while (!dq.empty() && dq.front() - cur > k2) {
            dq.pop_front();
        }
        // If deque is not empty, calculate the minimum cost
        if (!dq.empty()) {
            dp[cur] = dp[dq.front()] + abs(arr[cur] - arr[dq.front()]);
        }
        // Maintain the deque for minimum values
        while (!dq.empty() && dp[dq.back()] >= dp[cur]) {
            dq.pop_back();
        }
        dq.push_back(cur);
    }

    cout << dp[0];
    return 0;
}
