
#include "bits/stdc++.h"

#define ll long long

#define PI 3.1415926535897932384626433832795l

#define MAX_NUM 1000000000

using namespace std;

template <typename T> inline T __lcm(T a,T b)

{

    return (a*b)/__gcd(a,b);

}



ll h[1000000];

ll dp[1000000];

ll n,k;

ll fun(ll i)

{

    if(i>=n)

    {

        return MAX_NUM;

    }

    if(i==n-1)

    {

        return 0;

    }

    if(dp[i]!=-1)

    {

        return dp[i];

    }

    //return dp[i]=min(fabs(h[i]-h[i+1])+fun(i+1),fabs(h[i]-h[i+2])+fun(i+2));

    ll minimum=MAX_NUM;

    for(int j=1;j<=k;j++)

    {

        ll temp=fabs(h[i]-h[i+j])+fun(i+j);

        minimum=min(minimum,temp);

    }

    return dp[i]=minimum;

}



int main()

{

    cin>>n>>k;

    for(int i=0;i<n;i++)

    {

        cin>>h[i];

    }

    memset(dp,-1,sizeof(dp[0])*1000000);

    ll ans=fun(0);

    printf("%lld\n",ans);

}
