#include<iostream>
#include<cstdio>
#include<cstring>

using namespace std;

int n, k, f[1100000], w[1100000];

int xg(int x) {
    return x < 0 ? -x : x;
}

int main()
{
    cin >> n >> k;

    // Initializing only the first k elements to a large value.
    for(int i = 0; i <= k; i++) {
        f[i] = 0x3f3f3f3f;
    }

    for(int i = 0; i < n; i++)
    {
        cin >> w[i];
    }

    f[0] = 0;

    for(int i = 1; i < n; i++)
    {
        for(int j = 1; j <= k && (i - j) >= 0; j++)
        {
            f[i] = min(f[i], f[i - j] + xg(w[i - j] - w[i]));
        }
    }

    if (f[n - 1] == 0x3f3f3f3f) {
        cout << 0;
    } else {
        cout << f[n - 1];
    }
}
