using namespace std;

int main() {
    unordered_set<int> seen_numbers;
    int s;
    cin >> s;

    for(int i=0; ; i++) { // Start loop from 0 and remove unnecessary pre-check
        if (!seen_numbers.insert(s).second) { // Check and insert simultaneously
            cout << i + 1 << endl;
            return 0;
        }

        if(s % 2 == 0) {
            s /= 2;
        } else {
            s = 3*s + 1;
        }
    }
}
