using namespace std;

int main() {
    unordered_set<int> seen_numbers;
    int s;
    cin >> s;

    if (!seen_numbers.insert(s).second) { // Insert the initial number
        cout << 1 << endl;
        return 0;
    }

    for(int i=1; ; i++) { // Start loop from 1 since the initial number is already checked

        // Simplified logic
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }

        if (!seen_numbers.insert(s).second) { // Check and insert simultaneously
