#include <bits/stdc++.h>

using namespace std;

int main() {
    unordered_set<int> seen_numbers;
    int s;
    cin >> s;

    // Insert the initial number
    seen_numbers.insert(s);

    for (int i = 1; ; i++) { // Start loop from 1 since the initial number is already checked

        // Simplified logic
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }

        // Check if the number is already in the set
        if (seen_numbers.find(s) != seen_numbers.end()) {
            cout << i + 1 << endl;
            return 0;
        }
        seen_numbers.insert(s);
    }
}
