#include <bits/stdc++.h>

using namespace std;

int main() {
    unordered_set<int> seen_numbers;
    int s;
    cin >> s;

    // Insert the initial number
    seen_numbers.insert(s);

    int i = 1; // Initialize counter
    while (true) { // Use while loop for simplicity

        // Simplified logic
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }

        // Check if the number is already in the set
        if (seen_numbers.find(s) != seen_numbers.end()) {
            cout << i + 1 << endl;
            return 0;
        }
        seen_numbers.insert(s);
        i++; // Increment counter only here
    }
}
