#include <bits/stdc++.h>

#define ri register int 

#define ll long long 

const int maxn=200005;

const int inf=0x7fffffff;

int n,a[maxn],b[maxn];

ll sum[maxn<<2];

inline void read(int &x){
    x=0;int ne=0;char c;
    while(!isdigit(c=getchar()))ne=c=='-';
    x=c-48;
    while(isdigit(c=getchar()))x=(x<<3)+(x<<1)+c-48;
    x=ne?-x:x;return ;
}

inline void update(int x,int d){for(;x<=n;x+=x&(-x))sum[x]+=d;}

inline ll query(int x){ll ans=0;for(;x;x-=x&(-x))ans+=sum[x];return ans;}

inline ll chk(int k){
    ll ans=0; int cnt=0;
    for(ri i=1;i<=n;i++){
        b[i]=b[i-1]+((a[i]<=k)?1:-1);
        ans+=(b[i]>0);
    }
    std::map<int, int> c_map;
    for(ri i=0;i<=n;i++){
        if(!c_map.count(b[i])) c_map[b[i]] = ++cnt;
    }
    memset(sum,0,sizeof(sum));
    for(ri i=1;i<=n;i++){
        ans+=query(c_map[b[i]]-1);
        update(c_map[b[i]],1);
    }
    return ans;
}

int main(){
    read(n);
    int mi = inf, mx = -inf;
    for(ri i=1;i<=n;i++){
        read(a[i]);
        mi=std::min(mi,a[i]),mx=std::max(mx,a[i]);
    }
    int L=mi,R=mx,mid,ans;
    ll lim=1ll*n*(n+1)/4+1;
    while(L<=R){
        mid=(L+R)>>1;
        if(chk(mid)>=lim)ans=mid,R=mid-1;
        else L=mid+1;
    }
    printf("%d\n",ans);
    return 0;
}
