#include <bits/stdc++.h>

#define ll long long
#define endl "\n"

using namespace std;

inline int D()
{
    int t;
    cin >> t;
    return t;
}

inline ll llD()
{
    ll t;
    cin >> t;
    return t;
}

const int Max=1e6+9;
const ll INF=1e9;

ll a[Max];

void Fast()
{
    std::ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
    cerr.tie(0);
}

int main()
{
    Fast();

    int n = D();
    int m = D();

    for(int i=0; i<n; i++)
    {
        a[i] = llD();
    }

    vector<ll> dp(n, INF);
    dp[0] = 0;

    for(int i=0; i<n; i++)
    {
        for(int j=1; j<=m && i+j<n; j++)
        {
            dp[i+j] = min(dp[i+j], dp[i] + abs(a[i] - a[i+j]));
        }
    }

    cout << dp[n-1];

    return 0;
}
