#include <bits/stdc++.h>

#define ll long long
#define endl "\n"

using namespace std;

const int Max=1e6+9;
const ll INF=1e9;

ll a[Max];

int main()
{
    std::ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);

    int n, m;
    cin >> n >> m;

    for(int i=0; i<n; i++)
    {
        cin >> a[i];
    }

    vector<ll> dp(n, INF);
    dp[0] = 0;

    for(int i=0; i<n; i++)
    {
        for(int j=1; j<=m && i+j<n; j++)
        {
            dp[i+j] = min(dp[i+j], dp[i] + abs(a[i] - a[i+j]));
        }
    }

    cout << dp[n-1];

    return 0;
}
