#include <bits/stdc++.h>

using namespace std;

#define IO ios::sync_with_stdio(false), cin.tie(0)

typedef long long ll;

const int inf = 999999999;

int main()
{
    IO;

    int n, k;
    cin >> n >> k;

    vector<ll> arr(n + 1, 0);
    vector<ll> dp(n + 1, inf);

    for (int i = 1; i <= n; ++i)
        cin >> arr[i];

    dp[1] = 0;

    for (int i = 2; i <= n; ++i)
    {
        for (int j = 1; j <= k; ++j)
        {
            int prev_index = max(i - j, 1);
            dp[i] = min(dp[i], dp[prev_index] + abs(arr[i] - arr[prev_index]));
        }
    }

    cout << dp[n] << endl;
}
