#include <bits/stdc++.h>

using namespace std;

#define IO ios::sync_with_stdio(false), cin.tie(0)

typedef long long ll;

const int inf = 999999999;

int main()
{
    IO;

    int n, k;
    cin >> n >> k;

    vector<ll> arr(n + 1, 0);
    vector<ll> dp(n + 1, inf);

    for (int i = 1; i <= n; ++i)
        cin >> arr[i];

    dp[1] = 0;

    for (int i = 1; i < n; ++i)
    {
        for (int j = i + 1; j <= min(n, i + k); ++j)
        {
            dp[j] = min(dp[j], dp[i] + abs(arr[j] - arr[i]));
        }
    }

    cout << dp[n] << endl;
}
