#include<bits/stdc++.h>

using namespace std;

int main(){
    long long s, i(1);
    scanf("%lld", &s);
    if(s == 1 || s == 2 || s == 4) return printf("4");
    unordered_set<long long> seen;

    while(1){
        if(seen.find(s) != seen.end()) return 0 & printf("%lld", i);
        seen.insert(s);
        if(s & 1) s = 3 * s + 1;
        else s >>= 1;
        ++i;
    }
}
