#include <bits/stdc++.h>

using namespace std;

typedef long long int ll;

void solve(){

    int n, k, m; cin>>n>>k>>m;

    vector<int> a(n+1);

    for (int i = 1; i <= n; ++i)

    {

        cin>>a[i];

    }

    vector<int> d(n+2, INT_MAX);

    d[1] = 0;

    for (int i = 1; i <= n; ++i)

    {

        for(int j=1; j<=m; j++){

            int pos = i + j;

            if(pos <= n){

                d[pos] = min(d[pos], d[i] + abs(a[i] - a[pos]));

            }

        }

    }

    cout<<d[n]<<"\n";

}

int main(){

    ios_base::sync_with_stdio(false);

    cin.tie(nullptr);

    // ll t; cin>>t;

    // while(t--)

    solve();

    return 0;

}
