#include <iostream>

using namespace std;

typedef long long ll;

// 'A' = 65, 'Z' = 90, 'a' = 97, 'z' = 122


int main() {

    int s;

    cin >> s;

    bool flag[10000] = {};

    int a = s;

    flag[a] = true;

    int i = 1;

    while (1) {

        if (a % 2 == 0) a /= 2;

        else a = a * 3 + 1;

        if (flag[a]) {

            cout << i + 1 << endl;

            return 0;

        }

        flag[a] = true;

        i++;

    }

}
