#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

#define ll long long

ll MOD = 1e9 + 7;
int INF = 1 << 30;
ll INFL = 1LL << 60;

int main(){
  int N, K;
  cin >> N >> K;

  // Adjust the size of the vectors to be slightly larger than N for memory efficiency.
  vector<int> h(N + 1);
  vector<int> dp(N + 1, INF);

  for(int i = 1; i <= N; i++) {
    cin >> h.at(i);
  }

  dp[1] = 0;

  for(int i = 1; i < N; i++) {
    for(int j = 1; j <= K && i + j <= N; j++) {
      dp[i + j] = min(dp[i + j], dp[i] + abs(h[i + j] - h[i]));
    }
  }

  cout << dp[N] << endl;

  return 0;
}
